import useTranslation from "../../../../hooks/useTranslation";
import {
  Content,
  TopInfo,
  Number,
  RoleInfo,
  MockUp,
  ProjectFooter,
  MockUpContainer,
  Presentation,
  IconsContainer,
  Icon,
} from "./styles";

interface iProject {
  role: string;
  number: string;
  color: string;
  img: string;
  type: "mobile" | "desktop";
  title: string;
  description: string;
  source: string;
  deploy: string;
}

const Project = ({
  role,
  number,
  color,
  img,
  type,
  title,
  description,
  source,
  deploy,
}: iProject) => {
  const {t} = useTranslation();
  return (
    <Content color={color}>
      <TopInfo>
        <Number>
          .{number} <RoleInfo>[{role}]</RoleInfo>
        </Number>
      </TopInfo>
      <MockUpContainer>
        <MockUp src={img} type={type} alt={t("projectImage")} />
      </MockUpContainer>
      <ProjectFooter>
        <Presentation>
          <span>{title}</span>
          <strong>{description}</strong>
        </Presentation>
        <IconsContainer>
          <Icon
            href={source}
            target="_blank"
            rel="noopener noreferrer"
            title="Source"
            className="fab fa-github-square fa-2x"
          />
          <Icon
            href={deploy}
            target="_blank"
            rel="noopener noreferrer"
            title="Deploy"
            className="fas fa-satellite-dish fa-2x"
          />
        </IconsContainer>
        <Button icon="pi pi-user" severity="info" aria-label="User" />

      </ProjectFooter>
    </Content>
  );
};
export default Project;
